mapper.select:
select id, create_time, modify_time, status_zs,status,cert_password,err_code,err_msg,apply_id, etp_name,
etp_legal_name,
etp_addr, etp_phone, etp_province, etp_city, etp_tax_id, agent_name, agent_phone,
agent_id_card,seal_code,finance_code
from cert_apply_data


    id = #{id,jdbcType=VARCHAR}
    and create_time = #{createTime,jdbcType=TIMESTAMP}
    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}
    and status_zs = #{statusZs,jdbcType=INTEGER}
    and status = #{status,jdbcType=VARCHAR}
    and cert_password = #{certPassword,jdbcType=VARCHAR}
    and err_code = #{errCode,jdbcType=VARCHAR}
    and err_msg = #{errMsg,jdbcType=VARCHAR}
    and apply_id = #{applyId,jdbcType=VARCHAR}
    and etp_name = #{etpName,jdbcType=VARCHAR}
    and etp_legal_name = #{etpLegalName,jdbcType=VARCHAR}
    and etp_addr = #{etpAddr,jdbcType=VARCHAR}
    and etp_phone = #{etpPhone,jdbcType=VARCHAR}
    and etp_province = #{etpProvince,jdbcType=VARCHAR}
    and etp_city = #{etpCity,jdbcType=VARCHAR}
    and etp_tax_id = #{etpTaxId,jdbcType=VARCHAR}
    and agent_name = #{agentName,jdbcType=VARCHAR}
    and agent_phone = #{agentPhone,jdbcType=VARCHAR}
    and agent_id_card = #{agentIdCard,jdbcType=VARCHAR}

select COUNT(a.id)
from cert_apply_data a

    id = #{id,jdbcType=VARCHAR}
    and create_time = #{createTime,jdbcType=TIMESTAMP}
    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}
    and status_zs = #{statusZs,jdbcType=INTEGER}
    and status = #{status,jdbcType=VARCHAR}
    and cert_password = #{certPassword,jdbcType=VARCHAR}
    and err_code = #{errCode,jdbcType=VARCHAR}
    and err_msg = #{errMsg,jdbcType=VARCHAR}
    and apply_id = #{applyId,jdbcType=VARCHAR}
    and etp_name LIKE CONCAT('%',#{etpName,jdbcType=VARCHAR},'%')
    and etp_legal_name = #{etpLegalName,jdbcType=VARCHAR}
    and etp_addr = #{etpAddr,jdbcType=VARCHAR}
    and etp_phone = #{etpPhone,jdbcType=VARCHAR}
    and etp_province = #{etpProvince,jdbcType=VARCHAR}
    and etp_city = #{etpCity,jdbcType=VARCHAR}
    and etp_tax_id LIKE  CONCAT('%',#{etpTaxId,jdbcType=VARCHAR},'%')
    and agent_name = #{agentName,jdbcType=VARCHAR}
    and agent_phone = #{agentPhone,jdbcType=VARCHAR}
    and agent_id_card = #{agentIdCard,jdbcType=VARCHAR}

select *
from cert_apply_data

    id = #{id,jdbcType=VARCHAR}
    and create_time = #{createTime,jdbcType=TIMESTAMP}
    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}
    and status_zs = #{statusZs,jdbcType=INTEGER}
    and status = #{status,jdbcType=VARCHAR}
    and cert_password = #{certPassword,jdbcType=VARCHAR}
    and err_code = #{errCode,jdbcType=VARCHAR}
    and err_msg = #{errMsg,jdbcType=VARCHAR}
    and apply_id = #{applyId,jdbcType=VARCHAR}
    and etp_name LIKE CONCAT('%',#{etpName,jdbcType=VARCHAR},'%')
    and etp_legal_name = #{etpLegalName,jdbcType=VARCHAR}
    and etp_addr = #{etpAddr,jdbcType=VARCHAR}
    and etp_phone = #{etpPhone,jdbcType=VARCHAR}
    and etp_province = #{etpProvince,jdbcType=VARCHAR}
    and etp_city = #{etpCity,jdbcType=VARCHAR}
    and etp_tax_id LIKE  CONCAT('%',#{etpTaxId,jdbcType=VARCHAR},'%')
    and agent_name = #{agentName,jdbcType=VARCHAR}
    and agent_phone = #{agentPhone,jdbcType=VARCHAR}
    and agent_id_card = #{agentIdCard,jdbcType=VARCHAR}

ORDER by create_time desc limit #{pageOff},#{pageSize}
