mapper.select:
select id, fpdm, fphm, bz, nsrsbh, qqlsh, kpresult, parm1, parm2, parm3, parm4, parm5,
create_time, kp_json
from cd_efp_jp_invoice_copy

    id = #{id,jdbcType=VARCHAR}
    and fpdm = #{fpdm,jdbcType=VARCHAR}
    and fphm = #{fphm,jdbcType=VARCHAR}
      and bz = #{bz,jdbcType=INTEGER}
    and nsrsbh = #{nsrsbh,jdbcType=VARCHAR}
    and qqlsh = #{qqlsh,jdbcType=VARCHAR}
    and kpresult = #{kpresult,jdbcType=VARCHAR}
    and parm1 = #{parm1,jdbcType=VARCHAR}
    and parm2 = #{parm2,jdbcType=VARCHAR}
    and parm3 = #{parm3,jdbcType=VARCHAR}
    and parm4 = #{parm4,jdbcType=VARCHAR}
    and parm5 = #{parm5,jdbcType=VARCHAR}
    and create_time = #{createTime,jdbcType=TIMESTAMP}
    and kp_json = #{kpJson,jdbcType=LONGVARCHAR}

mapper.update:
update cd_efp_jp_invoice_copy
id = #{id,jdbcType=VARCHAR}, fpdm = #{fpdm,jdbcType=VARCHAR}, fphm = #{fphm,jdbcType=VARCHAR}, bz = #{bz,jdbcType=INTEGER}, nsrsbh = #{nsrsbh,jdbcType=VARCHAR}, qqlsh = #{qqlsh,jdbcType=VARCHAR}, kpresult = #{kpresult,jdbcType=VARCHAR}, parm1 = #{parm1,jdbcType=VARCHAR}, parm2 = #{parm2,jdbcType=VARCHAR}, parm3 = #{parm3,jdbcType=VARCHAR}, parm4 = #{parm4,jdbcType=VARCHAR}, parm5 = #{parm5,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, kp_json = #{kpJson,jdbcType=LONGVARCHAR},
where id = #{id,jdbcType=VARCHAR}
