mapper.select:
select id, wspzhm, fp_jym, jqbh, fpdm, fphm, kprq, jshj, hjse, hjje, sl, gfsh, gfmc,
xfsh, xfmc, xfdz_dh, xfyhmc_yhzh, swjgdm, swjgmc, mw, zf_bz, skr, fhr, kpr, tsnsrsbh,
bz, czy_id, phone_no, skm, create_time, parm1, parm2, parm3, parm4
from cd_efp_jp_invoice


    id = #{id,jdbcType=VARCHAR}
    and wspzhm = #{wspzhm,jdbcType=VARCHAR}
    and fp_jym = #{fpJym,jdbcType=VARCHAR}
    and jqbh = #{jqbh,jdbcType=VARCHAR}
    and fpdm = #{fpdm,jdbcType=VARCHAR}
    and fphm = #{fphm,jdbcType=VARCHAR}
    and kprq = #{kprq,jdbcType=DATE}
    and jshj = #{jshj,jdbcType=DECIMAL}
    and hjse = #{hjse,jdbcType=DECIMAL}
    and hjje = #{hjje,jdbcType=DECIMAL}
    and sl = #{sl,jdbcType=DECIMAL}
    and gfsh = #{gfsh,jdbcType=VARCHAR}
    and gfmc = #{gfmc,jdbcType=VARCHAR}
    and xfsh = #{xfsh,jdbcType=VARCHAR}
    and xfmc = #{xfmc,jdbcType=VARCHAR}
    and xfdz_dh = #{xfdzDh,jdbcType=VARCHAR}
    and xfyhmc_yhzh = #{xfyhmcYhzh,jdbcType=VARCHAR}
    and swjgdm = #{swjgdm,jdbcType=VARCHAR}
    and swjgmc = #{swjgmc,jdbcType=VARCHAR}
    and mw = #{mw,jdbcType=VARCHAR}
    and zf_bz = #{zfBz,jdbcType=INTEGER}
    and skr = #{skr,jdbcType=VARCHAR}
    and fhr = #{fhr,jdbcType=VARCHAR}
    and kpr = #{kpr,jdbcType=VARCHAR}
    and tsnsrsbh = #{tsnsrsbh,jdbcType=VARCHAR}
    and bz = #{bz,jdbcType=VARCHAR}
    and czy_id = #{czyId,jdbcType=VARCHAR}
    and phone_no = #{phoneNo,jdbcType=VARCHAR}
    and skm = #{skm,jdbcType=VARCHAR}
    and create_time = #{createTime,jdbcType=TIMESTAMP}
    and parm1 = #{parm1,jdbcType=VARCHAR}
    and parm2 = #{parm2,jdbcType=VARCHAR}
    and parm3 = #{parm3,jdbcType=VARCHAR}
    and parm4 = #{parm4,jdbcType=VARCHAR}

mapper.update:
update cd_efp_jp_invoice
