mapper.select:
select id, fpdm, fphm, wp_mc, wp_xh, wp_dw, sl, dj, je, se, hs_flag, czdate, swjg_dm,
xf_nsrsbh, gf_nsrsbh, wp_lb, zfbz, czdate_char, xhqd_bz, slv, fpqm_status, spbm,
qyspbm, syyhzcbz, lslbz, yhzc, wpsl
from cd_efp_zp_invoice_qd


    id = #{id,jdbcType=VARCHAR}
    and fpdm = #{fpdm,jdbcType=CHAR}
    and fphm = #{fphm,jdbcType=CHAR}
    and wp_mc = #{wpMc,jdbcType=VARCHAR}
    and wp_xh = #{wpXh,jdbcType=VARCHAR}
    and wp_dw = #{wpDw,jdbcType=VARCHAR}
    and sl = #{sl,jdbcType=DECIMAL}
    and dj = #{dj,jdbcType=DECIMAL}
    and je = #{je,jdbcType=DECIMAL}
    and se = #{se,jdbcType=DECIMAL}
    and hs_flag = #{hsFlag,jdbcType=VARCHAR}
    and czdate = #{czdate,jdbcType=TIMESTAMP}
    and swjg_dm = #{swjgDm,jdbcType=VARCHAR}
    and xf_nsrsbh = #{xfNsrsbh,jdbcType=VARCHAR}
    and gf_nsrsbh = #{gfNsrsbh,jdbcType=VARCHAR}
    and wp_lb = #{wpLb,jdbcType=VARCHAR}
    and zfbz = #{zfbz,jdbcType=VARCHAR}
    and czdate_char = #{czdateChar,jdbcType=VARCHAR}
    and xhqd_bz = #{xhqdBz,jdbcType=VARCHAR}
    and slv = #{slv,jdbcType=DECIMAL}
    and fpqm_status = #{fpqmStatus,jdbcType=VARCHAR}
    and spbm = #{spbm,jdbcType=VARCHAR}
    and qyspbm = #{qyspbm,jdbcType=VARCHAR}
    and syyhzcbz = #{syyhzcbz,jdbcType=VARCHAR}
    and lslbz = #{lslbz,jdbcType=VARCHAR}
    and yhzc = #{yhzc,jdbcType=VARCHAR}
    and wpsl = #{wpsl,jdbcType=INTEGER}

mapper.update:
update cd_efp_zp_invoice_qd
id = #{id,jdbcType=VARCHAR}, fpdm = #{fpdm,jdbcType=CHAR}, fphm = #{fphm,jdbcType=CHAR}, wp_mc = #{wpMc,jdbcType=VARCHAR}, wp_xh = #{wpXh,jdbcType=VARCHAR}, wp_dw = #{wpDw,jdbcType=VARCHAR}, sl = #{sl,jdbcType=DECIMAL}, dj = #{dj,jdbcType=DECIMAL}, je = #{je,jdbcType=DECIMAL}, se = #{se,jdbcType=DECIMAL}, hs_flag = #{hsFlag,jdbcType=VARCHAR}, czdate = #{czdate,jdbcType=TIMESTAMP}, swjg_dm = #{swjgDm,jdbcType=VARCHAR}, xf_nsrsbh = #{xfNsrsbh,jdbcType=VARCHAR}, gf_nsrsbh = #{gfNsrsbh,jdbcType=VARCHAR}, wp_lb = #{wpLb,jdbcType=VARCHAR}, zfbz = #{zfbz,jdbcType=VARCHAR}, czdate_char = #{czdateChar,jdbcType=VARCHAR}, xhqd_bz = #{xhqdBz,jdbcType=VARCHAR}, slv = #{slv,jdbcType=DECIMAL}, fpqm_status = #{fpqmStatus,jdbcType=VARCHAR}, spbm = #{spbm,jdbcType=VARCHAR}, qyspbm = #{qyspbm,jdbcType=VARCHAR}, syyhzcbz = #{syyhzcbz,jdbcType=VARCHAR}, lslbz = #{lslbz,jdbcType=VARCHAR}, yhzc = #{yhzc,jdbcType=VARCHAR}, wpsl = #{wpsl,jdbcType=INTEGER},
where id = #{id,jdbcType=VARCHAR}
